package budget.application;

import budget.application.common.Constants;
import budget.application.db.util.DataSourceFactory;
import budget.application.db.util.DatabaseHealthCheck;
import budget.application.scheduler.ScheduleManager;
import budget.application.server.core.ServerNetty;
import io.github.bibekaryal86.shdsvc.Email;
import io.github.bibekaryal86.shdsvc.helpers.CommonUtilities;
import java.util.ArrayList;
import java.util.List;
import java.util.Map;
import javax.sql.DataSource;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

public class Main {
  private static final Logger log = LoggerFactory.getLogger(Main.class);

  public static void main(String[] args) throws Exception {
    log.info("Starting Budget Service...");
    Main.checkEnvProperties();

    Email email = new Email();
    DataSource dataSource = DataSourceFactory.create();

    var dbHealth = new DatabaseHealthCheck(dataSource).check();
    log.info("{}", dbHealth);

    ServerNetty serverNetty = new ServerNetty(dataSource, email);
    serverNetty.start();

    ScheduleManager schedulerManager = new ScheduleManager(dataSource, email);
    schedulerManager.start();

    Runtime.getRuntime()
        .addShutdownHook(
            new Thread(
                () -> {
                  log.info("Shutdown initiated...");
                  try {
                    schedulerManager.shutdown();
                  } catch (Exception e) {
                    log.error("Error shutting down scheduler... ", e);
                  }
                  try {
                    serverNetty.stop();
                  } catch (Exception e) {
                    log.error("Error shutting down server... ", e);
                  }
                  log.info("Shutdown complete...");
                },
                "shutdown-hook"));

    log.info("Started Budget Service...");
  }

  private static void checkEnvProperties() {
    final List<String> envKeyNames = new ArrayList<>(Constants.ENV_KEY_NAMES);

    if (Constants.IS_PRODUCTION) {
      envKeyNames.addAll(Constants.ENV_KEY_NAMES_PROD);
    } else {
      envKeyNames.addAll(Constants.ENV_KEY_NAMES_SANDBOX);
    }

    final Map<String, String> envProperties = CommonUtilities.getSystemEnvProperties(envKeyNames);
    final List<String> requiredEnvProperties =
        envKeyNames.stream().filter(key -> !Constants.ENV_SERVER_PORT.equals(key)).toList();

    final List<String> errors =
        requiredEnvProperties.stream().filter(key -> envProperties.get(key) == null).toList();
    if (!errors.isEmpty()) {
      throw new IllegalStateException(
          "One or more environment configurations could not be accessed...");
    }
  }
}
